$(document).ready(function () {
  var t = setInterval(function () {
    $(".eapps-form-element-container").length &&
      (e(),
      $("body").on(
        "click",
        ".eapps-form-success-button.eapps-form-button",
        function () {
          e(), $("#contactModal").modal("hide");
        }
      ),
      clearInterval(t));
  }, 100);
  function e() {
    $("body")
      .find(
        ".eapps-form-element-container input,.eapps-form-element-container textarea"
      )
      .on("focus", function () {
        $(this)
          .parents(".eapps-form-element-wrapper")
          .find(".eapps-form-element-label")
          .addClass("focus"),
          $(this).parents(".eapps-form-element-wrapper").removeClass("error");
      }),
      $("body")
        .find(
          ".eapps-form-element-container input,.eapps-form-element-container textarea"
        )
        .each(function () {
          0 < $(this).val().length &&
            ($(this)
              .parents(".eapps-form-element-wrapper")
              .find(".eapps-form-element-label")
              .addClass("focus"),
            $(this)
              .parents(".eapps-form-element-wrapper")
              .removeClass("error"));
        }),
      $("body")
        .find(
          ".eapps-form-element-container input,.eapps-form-element-container textarea"
        )
        .on("blur", function () {
          0 === $(this).val().length &&
            ($(this)
              .parents(".eapps-form-element-wrapper")
              .find(".eapps-form-element-label")
              .removeClass("focus"),
            $(this)
              .parents(".eapps-form-element-wrapper")
              .removeClass("error"));
        }),
      $("#contactModal").on("click", function (t) {
        $(t.target).closest(".modal-content").length < 1 &&
          setTimeout(function () {
            $("#contactModal").find(".form-group").removeClass("error");
          }, 500);
      }),
      $("body")
        .find(".eapps-form-fieldset")
        .prepend(
          "<h3>Let's Talk!</h3><p class='eapps-subtitle'>We will get back to you within one to two days through email. Also please don't forget to check your spam account just in case!</p>"
        );
  }
});
